package com.codegym.service;


import com.codegym.model.Product;

import java.util.ArrayList;
import java.util.List;

public class ProductService implements GeneralService<Product> {
    private static List<Product> products = new ArrayList<>();
    private static CategoryService categoryService = new CategoryService();

    static {
        products.add(new Product(1, "NoKia 1080", 800, "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ3tnZkHPBn-qxg_hPgu3yAAtSPcdrD2Eu6zg&usqp=CAU", categoryService.findById(1), true));
        products.add(new Product(2, "Iphone ProMax", 1000, "data:image/jpeg;base64,", categoryService.findById(1), true));
        products.add(new Product(3, "SamSung A380", 920, "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSMayNFvxEUBdcYWYqJBlqlZ6qfSmOwbanv9Q&usqp=CAU", categoryService.findById(1), false));
    }

    @Override
    public List<Product> getAll() {
        return products;
    }

    @Override
    public void save(Product product) {
        products.add(product);
    }

    @Override
    public Product findById(int id) {
        for (Product p : products
        ) {
            if (p.getIdProduct() == id) {
                return p;
            }
        }
        return null;
    }

    @Override
    public void update(int id, Product product) {
        for (int i = 0; i < products.size(); i++) {
            if (products.get(i).getIdProduct() == id) {
                products.set(i, product);
            }
        }
    }

    @Override
    public void remove(int id) {
        products.remove(findById(id));
    }
    public List<Product> SearchByName(String name){
        List<Product> productList = new ArrayList<>();
        for (Product p:products
        ) {
            if (p.getNameProduct().contains(name)){
                productList.add(p);
            }
        }
        return productList;
    }
}